Error.getInitialProps = ({ res, err }) => {
  const statusCode = res ? res.statusCode : err ? err.statusCode : 404;

  // Report error to Revi
  if (err) {
    revi.captureException(err, {
      tags: {
        statusCode: statusCode,
        context: 'pages-error',
      },
    });
  }

  return { statusCode };
};

export default Error;
