## Environment Configuration

### Configuration Manager

```javascript
// config.js
export class Config {
  constructor() {
    this.env = this.detectEnvironment();
    this.config = this.getConfig();
  }

  detectEnvironment() {
    if (typeof window !== 'undefined') {
      const hostname = window.location.hostname;
      if (hostname === 'localhost' || hostname === '127.0.0.1') {
        return 'development';
      } else if (hostname.includes('staging')) {
        return 'staging';
      } else {
        return 'production';
      }
    }
    return 'production';
  }

  getConfig() {
    const configs = {
      development: {
        reviApiKey: 'revi_dev_key',
        reviApiUrl: 'http://localhost:4000',
        debug: true,
        sessionSampleRate: 1.0,
      },
      staging: {
        reviApiKey: 'revi_staging_key',
        reviApiUrl: 'https://staging-revi-backend.com',
        debug: true,
        sessionSampleRate: 0.5,
      },
      production: {
        reviApiKey: 'revi_prod_key',
        reviApiUrl: 'https://your-revi-backend.com',
        debug: false,
        sessionSampleRate: 0.1,
      },
    };

    return configs[this.env];
  }

  get(key) {
    return this.config[key];
  }
}
