### Form Component with Validation

```svelte
<!-- src/lib/components/ContactForm.svelte -->
<script>
  import { captureException, captureAsyncError, addBreadcrumb } from '$lib/stores/revi';

  let email = '';
  let message = '';
  let errors = {};
  let isSubmitting = false;

  function validateForm() {
    errors = {};

    if (!email) {
      errors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(email)) {
      errors.email = 'Email is invalid';
    }

    if (!message) {
      errors.message = 'Message is required';
    }

    return Object.keys(errors).length === 0;
  }

  async function handleSubmit() {
    addBreadcrumb('Form submission started');

    if (!validateForm()) {
      captureException(new Error('Form validation failed'), {
        component: 'ContactForm',
        validationErrors: Object.keys(errors),
      });
      return;
    }

    isSubmitting = true;

    try {
      await captureAsyncError(
        async () => {
          const response = await fetch('/api/contact', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ email, message }),
          });

          if (!response.ok) {
            throw new Error(`Form submission failed: ${response.status}`);
          }

          return response.json();
        },
        {
          component: 'ContactForm',
          formFields: ['email', 'message'], // Don't send actual form data
        }
      );

      addBreadcrumb('Form submitted successfully');

      // Reset form
      email = '';
      message = '';
      errors = {};

    } catch (error) {
      // Error already captured by captureAsyncError
      console.error('Form submission failed:', error);
    } finally {
      isSubmitting = false;
    }
  }

  // Track form interactions
  function trackFieldChange(fieldName) {
    addBreadcrumb(`Form field changed: ${fieldName}`, {
      component: 'ContactForm',
      field: fieldName,
    });
  }
</script>

<form on:submit|preventDefault={handleSubmit}>
  <div class="form-group">
    <label for="email">Email</label>
    <input
      id="email"
      type="email"
      bind:value={email}
      on:input={() => trackFieldChange('email')}
      class:error={errors.email}
      placeholder="Your email address"
    />
    {#if errors.email}
      <div class="error-message">{errors.email}</div>
    {/if}
  </div>

  <div class="form-group">
    <label for="message">Message</label>
    <textarea
      id="message"
      bind:value={message}
      on:input={() => trackFieldChange('message')}
      class:error={errors.message}
      placeholder="Your message"
      rows="4"
    ></textarea>
    {#if errors.message}
      <div class="error-message">{errors.message}</div>
    {/if}
  </div>

  <button type="submit" disabled={isSubmitting}>
    {isSubmitting ? 'Submitting...' : 'Submit'}
  </button>
</form>

<style>
  .form-group {
    margin-bottom: 1rem;
  }

  label {
    display: block;
    margin-bottom: 0.5rem;
    font-weight: 500;
  }

  input, textarea {
    width: 100%;
    padding: 0.5rem;
    border: 1px solid #ddd;
    border-radius: 4px;
    font-size: 1rem;
  }

  input:focus, textarea:focus {
    outline: none;
    border-color: #3b82f6;
  }

  input.error, textarea.error {
    border-color: #ef4444;
  }

  .error-message {
    color: #ef4444;
    font-size: 0.875rem;
    margin-top: 0.25rem;
  }

  button {
    background-color: #3b82f6;
    color: white;
    padding: 0.75rem 1.5rem;
    border: none;
    border-radius: 4px;
    cursor: pointer;
    font-size: 1rem;
  }

  button:hover:not(:disabled) {
    background-color: #2563eb;
  }

  button:disabled {
    background-color: #9ca3af;
    cursor: not-allowed;
  }
</style>
